CREATE TABLE IF NOT EXISTS `yun_members` (  `id` int(20) NOT NULL AUTO_INCREMENT,  `groupid` int(3) NOT NULL,  `account` varchar(30) NOT NULL,  `password` varchar(60) NOT NULL,  `rmb` int(8) NOT NULL DEFAULT '0',  `crmb` int(8) NOT NULL DEFAULT '0',  `nickname` varchar(30) NOT NULL,  `email` varchar(30) NOT NULL,  `tel` varchar(15) NOT NULL,  `qq` varchar(20) NOT NULL,  `regtime` int(11) NOT NULL,  `regip` varchar(16) NOT NULL,  `lasttime` int(11) NOT NULL,  `lastip` varchar(16) NOT NULL,  `islock` tinyint(4) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `yun_member_group` (  `id` int(3) NOT NULL AUTO_INCREMENT,  `name` varchar(30) NOT NULL,  `notallow` text NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `yun_orders` (  `id` int(15) NOT NULL AUTO_INCREMENT,  `ordernum` varchar(20) NOT NULL COMMENT ' 订单号',  `account` varchar(30) NOT NULL COMMENT '账户',  `total` float NOT NULL COMMENT '总价',  `freight` float NOT NULL COMMENT '运费',  `ordertime` int(11) NOT NULL COMMENT '订单时间',  `state` tinyint(1) NOT NULL COMMENT '订单状态',  `mess` text NOT NULL COMMENT '订单信息',  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `yun_order_detail` (  `id` int(20) NOT NULL AUTO_INCREMENT,  `code` varchar(10) NOT NULL COMMENT '商品编号',  `ordernum` varchar(20) NOT NULL,  `name` varchar(100) NOT NULL,  `price` float NOT NULL,  `num` int(5) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;
